import React from 'react';
import { HashRouter as Router, NavLink, Route, Switch} from 'react-router-dom';
import './App.css';
import {Container,Navbar,Nav,NavItem } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

// Install the socket io client using:
//    npm install socket.io
//
// Then import socket io and create a socket:
//
import io from 'socket.io-client';
const socket = io('http://localhost:3001');
//
// See: https://socket.io/get-started/chat
//      https://www.npmjs.com/package/socket.io-client

class LiveFeed extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      showEventFilters: false,
      showPriorityFilters: false,
      filterFire: false,
      filterFlood: false,
      filterPower: false,
      filterMedical: false,
      filterLow: false,
      filterMedium: false,
      filterHigh: false,
      filterCritical: false};
  }

  showEventFilters(){ 

    if(this.state.showEventFilters == false){
      document.getElementById("eventFilter").style.display = "";
      this.state.showEventFilters = true;
    } 

    else{
      document.getElementById("eventFilter").style.display = "none";
      this.state.showEventFilters = false;
    }
}

showPriorityFilters(){ 

  if(this.state.showPriorityFilters == false){
    document.getElementById("priorityFilter").style.display = "";
    this.state.showPriorityFilters = true;
  } 

  else{
    document.getElementById("priorityFilter").style.display = "none";
    this.state.showPriorityFilters = false;
  }
}

flipFire() { this.setState({filterFire: !this.state.filterFire  }); }

flipFlood() { this.setState({filterFlood: !this.state.filterFlood  }); }


  render() {
    return (
        <div>
          <h2>LiveFeed</h2>
          {this.props.posts.map( 
          ({name,image,content,problem,priority,id}) => 
            <div key={id}> 
              <img src={image} alt="{name}" /> <br />
                {name} <br />
                {problem} <br />
                {priority} <br />
                {content} <br /> <br /> <br /> <br />
            </div> 
          )}

        <button onClick={this.showEventFilters.bind(this)} class="btn btn-primary" style={{marginBottom: '6px'}} id="eventFilters">Show Event Filters</button> 

        <button onClick={this.showPriorityFilters.bind(this)} class="btn btn-primary" style={{marginBottom: '6px'}} id="priorityFilters">Show Priority Filters</button> 

        <div id="eventFilter" style={{display: 'none'}}>

        <input type="checkbox" id="flipFire" checked={this.state.filterFire} 
                   onChange={this.flipFire.bind(this)} />
        <label for="filterFire">Fire</label>

        <input type="checkbox" id="flipFlood" checked={this.state.filterFlood} 
                   onChange={this.flipFlood.bind(this)} />
        <label for="filterFlood">Flood</label>

        <input type="checkbox" id="flipPower" checked={this.state.filterPower} 
                   onChange={this.flipPower.bind(this)} />
        <label for="filterPower">Power</label>

        <input type="checkbox" id="flipMedical" checked={this.state.filterMedical} 
                   onChange={this.flipFire.bind(this)} />
        <label for="filterMedical">Medical</label>

        </div>

        <div id="Priority Filter" style={{display: 'none'}}>
        <input type="checkbox" id="flipLow" checked={this.state.filterLow} 
                   onChange={this.flipLow.bind(this)} />
        <label for="filterLow">Low</label>

        <input type="checkbox" id="flipMedium" checked={this.state.filterMedium} 
                   onChange={this.flipMedium.bind(this)} />
        <label for="filterMedium">Medium</label>

        <input type="checkbox" id="flipHigh" checked={this.state.filterHigh} 
                   onChange={this.flipHigh.bind(this)} />
        <label for="filterHigh">High</label>

        <input type="checkbox" id="flipCritical" checked={this.state.filterCritical} 
                   onChange={this.flipCritical.bind(this)} />
        <label for="filterCritical">Critical</label>
        </div>
        </div>
    );
  }
}

class Analytics extends React.Component {

  render() {
    return (
        <div>
          <h2>Analytics</h2>
        </div>
    );
  }
}

class Home extends React.Component {
  render() {
    return (
        <div>
          <h2>Home</h2>
        </div>
    );
  }
}

class App extends React.Component {

  constructor(props) {
    super(props);

    // An array of social media posts messages, and we'll increment nextID
    // to maintain a unique ID for each post in our array
    this.state = {posts: [],nextID: 0};

    // We can setup the event handlers for the messages in the constructor...
    socket.on('connect', function(){

      console.log("Connect....");

      // When we receive a social media message, call setState and insert 
      // it into the array of posts
      socket.on('post', 

        function(data) {

          // Can uncomment this to see the raw data coming in...
          // console.log("post: " + JSON.stringify(data));

          // increment the next unique ID, and put post data into the list of 
          // posts... use the '...' syntax to make this insertion easier:
          // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Operators/Spread_syntax 
          this.setState( 
            {posts: [...this.state.posts,
                     {name: data.name, 
                      image: data.image, 
                      content: data.content, 
                      problem: data.problem,
                      priority: data.priority,
                      id: this.state.nextID}]
            ,nextID: this.state.nextID + 1} );
        }.bind(this));
        // ^^ Like other event handlers, we bind the callback function to the 
        // component so we can use setState        

    }.bind(this));
    // ^^ ... And same with the callback function for when a connection is made

  }

  // Output all the posts into a table
  render() {
    return (
      <div>
        <Router>
        <div>
          <Navbar bg="dark" variant="dark">
            <Navbar.Collapse>
              <Nav className="mr-auto">
                <NavItem eventkey={1} href="/home">
                  <Nav.Link as={NavLink} to="/home" >Home</Nav.Link>
                </NavItem>
                <NavItem eventkey={2} href="/livefeed">
                  <Nav.Link as={NavLink} to="/livefeed" >LiveFeed</Nav.Link>
                </NavItem>
                <NavItem eventkey={3} href="/analytics">
                  <Nav.Link as={NavLink} to="/analytics" >Analytics</Nav.Link>
                </NavItem>
              </Nav>
            </Navbar.Collapse>
          </Navbar>
        </div>

        {/* <Navbar bg="light" expand="lg">
              <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="mr-auto">
                <NavItem>
                  <NavLink to='/home'>Home</NavLink>
                  </NavItem>
                <NavItem>
                <NavLink to='/liveFeed'>LiveFeed</NavLink>
                </NavItem>
                <NavItem>
                <NavLink to='/analytics'>Analytics</NavLink>
                </NavItem>
                </Nav>
                </Navbar.Collapse>
                </Navbar> */}

              <Route path='/home' render={(props) => <Home {...props} posts={this.state.posts}/> } />
              <Route path='/livefeed' render={(props) => <LiveFeed {...props} posts={this.state.posts}/> }/>
              <Route path='/analytics' render={(props) => <Analytics {...props} posts={this.state.posts}/> } />
            </Router>
      </div>
    );
  }
}

export default App;
